var msgneu={};
msgneu.to=msg.from;
msgneu.topic=msg.topic;
msgneu.payload="automatisch erzeugte Email vom Heizungsraspberrypi\n\n";
var notifications=global.get("notifications");
for (var i=0;i<notifications.length;i++) {
  msgneu.payload+=notifications[i]+"\n";
  }
msgneu.payload+="\n"+"Antwort auf Email "+msg.date;
return msgneu;

var t=msg.payload;
var notifications=global.get("notifications")||[];
var d=new Date();
var ind=t.indexOf("Notification");
var tneu=d.toString()+"  "+t.slice(ind,ind+53);
if (ind>-1) notifications.push(tneu);
while (notifications.length>8) notifications.shift(t);
global.set("notifications",notifications);
if (ind>-1) {
  msg.payload=tneu;
  return msg;
  }

msg=msg.payload;
var t=new Date();
msg.date=t.toString();
return msg;

var p=msg.payload;
var msg1=null; if (p.slice(0,10)=="heizung an") {msg1={}; msg1.payload="Heizung an"};
var msg2=null; if (p.slice(0,11)=="heizung aus") {msg2={}; msg2.payload="Heizung aus"};
var msg3=null; if (p.slice(0,6)=="status") {msg3={}; msg3.payload="Status"};
var msg4=null; if (p.slice(0,6)=="reboot") {msg4={}; msg4.payload="Reboot"};
return [msg1,msg2,msg3,msg4];
